// Access the webhook payload from the correct location
const webhookData = $input.first().json;
const payload = webhookData.body || webhookData;

// Extract required fields
const userId = payload.user_id || payload.userId;
const storyId = payload.story_id || payload.storyId;
const concept = payload.concept || {};
const initialConcept = concept.initialConcept || payload.initialConcept;

// Validation
if (!userId) throw new Error('Missing user_id field');
if (!storyId) throw new Error('Missing story_id field');
if (!initialConcept) throw new Error('Missing concept.initialConcept field');

// Return validated data
return {
  jobId: `ai_create_gemini_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  userId,
  storyId,
  concept: concept,
  optimizedPrompt: payload.optimized_prompt || '',
  characters: payload.characters || [],
  preferences: payload.preferences || {},
  generation_metadata: payload.generation_metadata || {}
};

// Handle Gemini response format
const geminiInput = $input.first().json;
let rawResponse;

console.log('Full Gemini Input received:', JSON.stringify(geminiInput, null, 2));

// Extract text from Gemini response structure - UPDATED LOGIC
if (geminiInput.content && geminiInput.content.parts && geminiInput.content.parts[0]) {
  // Gemini API v1 format (current)
  rawResponse = geminiInput.content.parts[0].text;
} else if (geminiInput.candidates && geminiInput.candidates[0] && geminiInput.candidates[0].content && geminiInput.candidates[0].content.parts) {
  // Gemini API alternative format
  rawResponse = geminiInput.candidates[0].content.parts[0].text;
} else if (geminiInput.response) {
  // Alternative Gemini format
  rawResponse = geminiInput.response;
} else if (geminiInput.text) {
  // Direct text format
  rawResponse = geminiInput.text;
} else if (typeof geminiInput === 'string') {
  // String response
  rawResponse = geminiInput;
} else {
  // Debug: show what we actually received
  throw new Error(`Unexpected Gemini response format. Received: ${JSON.stringify(geminiInput, null, 2)}`);
}

// Try to get job data
let jobData;
try {
  jobData = $('Validate Input').first().json;
} catch (e) {
  // Create a fallback job data
  jobData = {
    storyId: "fallback-story-id",
    jobId: `ai_create_gemini_${Date.now()}_fallback`
  };
}

console.log('Extracted Gemini Response:', rawResponse);
console.log('Response length:', rawResponse ? rawResponse.length : 'null/undefined');

// Check if response is empty
if (!rawResponse || rawResponse.trim() === '') {
  throw new Error(`Gemini returned empty response. Check: 1) Prompt format, 2) Model configuration, 3) Token limits.`);
}

// Clean the response - Gemini sometimes adds markdown formatting
let cleanedResponse = rawResponse;
// Remove markdown code blocks if present
cleanedResponse = cleanedResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '');
// Remove any leading/trailing whitespace
cleanedResponse = cleanedResponse.trim();

// Parse the cleaned response as JSON
try {
  const aiResponse = JSON.parse(cleanedResponse);

  // Validate response structure
  if (!aiResponse.script || !aiResponse.script.lines) {
    throw new Error(`Invalid Gemini response structure. Expected script.lines, got: ${Object.keys(aiResponse)}`);
  }

  // Create script record with additional metadata
  const scriptRecord = {
    story_id: jobData.storyId,
    title: aiResponse.script.title || 'Gemini Generated Script',
    generation_method: 'ai_assisted',
    ai_generation_job_id: jobData.jobId,
    target_age: aiResponse.script.target_age || '5-10',
    duration_minutes: aiResponse.script.duration_minutes || 9.5
  };

  // Process script lines with enhanced audio fields
  const scriptLines = aiResponse.script.lines.map((line, index) => {
    // Validate and clean the line data
    const processedLine = {
      line_number: index + 1,
      type: line.type || 'dialogue',
      speaker: line.speaker || 'Narrator',
      text: line.text || '[Empty line]',
      emotion: line.emotion || 'Neutral',
      ai_generated: true,
      ai_confidence: 0.95,  // Higher confidence for Gemini 2.5
      needs_review: !line.text || line.text.trim() === ''
    };

    // Add audio-specific fields if they exist
    if (line.pace) {
      processedLine.pace = line.pace;
    }

    if (line.pause_after !== undefined) {
      processedLine.pause_after = line.pause_after;
    } else {
      // Default pause based on line type
      processedLine.pause_after = line.type === 'narration' ? 1.0 : 0.5;
    }

    // Extract sound effects if present in the text
    if (line.text && line.text.includes('[SOUND:')) {
      const soundMatch = line.text.match(/\[SOUND:\s*([^\]]+)\]/);
      if (soundMatch) {
        processedLine.sound_effect = soundMatch[1].trim();
        // Remove sound effect tag from text for cleaner audio
        processedLine.text = line.text.replace(/\[SOUND:[^\]]+\]/g, '').trim();
      }
    }

    // Extract scene transitions
    if (line.text && line.text.includes('[PAUSE')) {
      const pauseMatch = line.text.match(/\[PAUSE\s*-\s*([\d.]+)\s*seconds?\]/i);
      if (pauseMatch) {
        processedLine.pause_after = parseFloat(pauseMatch[1]);
        // Remove pause tag from text
        processedLine.text = line.text.replace(/\[PAUSE[^\]]+\]/g, '').trim();
      }
    }

    return processedLine;
  });

  console.log(`Successfully processed Gemini response: ${scriptLines.length} lines`);
  console.log(`Estimated duration: ${aiResponse.script.duration_minutes || 'unknown'} minutes`);

  // Calculate estimated audio duration for validation
  const estimatedSeconds = scriptLines.reduce((total, line) => {
    const wordCount = line.text.split(' ').length;
    const speakingTime = wordCount / 2.5; // Average speaking rate: 150 words/minute
    const pauseTime = line.pause_after || 0.5;
    return total + speakingTime + pauseTime;
  }, 0);

  const estimatedMinutes = estimatedSeconds / 60;
  console.log(`Calculated duration: ${estimatedMinutes.toFixed(1)} minutes`);

  // Warn if duration seems off
  if (estimatedMinutes < 8 || estimatedMinutes > 12) {
    console.warn(`Warning: Estimated duration (${estimatedMinutes.toFixed(1)} min) outside target range (9-10 min)`);
  }

  return { 
    scriptRecord, 
    scriptLines, 
    aiResponse,
    metadata: {
      estimatedDuration: estimatedMinutes,
      totalLines: scriptLines.length,
      dialogueLines: scriptLines.filter(l => l.type === 'dialogue').length,
      narrationLines: scriptLines.filter(l => l.type === 'narration').length
    }
  };

} catch (parseError) {
  console.error('Failed to parse Gemini response as JSON:', parseError.message);
  console.error('Cleaned response:', cleanedResponse);
  throw new Error(`Failed to parse Gemini response as JSON: ${parseError.message}. Response: ${cleanedResponse.substring(0, 500)}...`);
}

// Get the script content from Process Gemini Response
const geminiData = $('Process Gemini Response').first().json;

console.log('=== PREPARE SCRIPT LINES DEBUG (GEMINI) ===');
console.log('Gemini data keys:', Object.keys(geminiData));

// Get the script_id from the Create Script node
const scriptId = $('Create Script').first().json.id;
console.log('Script ID from Create Script node:', scriptId);

// Function to map Gemini emotions to valid DB emotions
function mapEmotionToValid(emotion) {
  const emotionMap = {
    'warm': 'happy',
    'mysterious': 'neutral',
    'ominous': 'scared',
    'urgent': 'excited',
    'determined': 'excited',
    'worried': 'sad',
    'hopeful': 'happy',
    'descriptive': 'neutral',
    'confused': 'neutral',
    'reassuring': 'happy',
    'brave': 'excited',
    'tense': 'scared',
    'curious': 'excited',
    'knowing': 'neutral',
    'triumphant': 'excited',
    'reflective': 'neutral',
    'kind': 'happy'
  };

  const lowerEmotion = (emotion || 'neutral').toLowerCase();
  return emotionMap[lowerEmotion] || 'neutral';
}

// Get the script lines from Gemini processing
let scriptLines = [];

if (geminiData.scriptLines && Array.isArray(geminiData.scriptLines)) {
  // Use the processed script lines from Gemini
  scriptLines = geminiData.scriptLines.map((line, index) => ({
    script_id: scriptId,
    line_number: line.line_number || (index + 1),
    speaker_name: line.speaker || 'Narrator',
    line_type: line.type || 'dialogue',
    text_content: line.text || '[Empty line]',
    emotion_type: mapEmotionToValid(line.emotion),
    emotion_intensity: 'medium',
    is_generated: true
  }));

  console.log('Using Gemini script lines:', scriptLines.length, 'lines');
} else {
  // Fallback if no script lines found
  console.log('No script lines found, using fallback');
  const fallbackScript = `FADE IN:\n\nINT. SCENE - DAY\n\nNARRATOR\n(calm)\nOur story begins with Gemini AI...\n\nCHARACTER\n(excited)\nThis is a Gemini-generated placeholder script!\n\nNARRATOR\n(measured)\nThe story continues with enhanced audio features.\n\nFADE OUT.`;

  const lines = fallbackScript.split('\n').filter(line => line.trim() !== '');

  scriptLines = lines.map((line, index) => {
    const trimmedLine = line.trim();

    let lineType = 'dialogue';
    let speakerName = 'Narrator';

    if (trimmedLine.startsWith('FADE') || trimmedLine.startsWith('INT.') || trimmedLine.startsWith('EXT.')) {
      lineType = 'stage_direction';
      speakerName = 'Narrator';
    } else if (trimmedLine.match(/^[A-Z\s]+$/) && !trimmedLine.includes(':') && !trimmedLine.includes('.')) {
      lineType = 'dialogue';
      speakerName = trimmedLine.trim();
    } else if (trimmedLine.match(/^\((.+)\)$/)) {
      lineType = 'stage_direction';
      speakerName = 'Narrator';
    }

    return {
      script_id: scriptId,
      line_number: index + 1,
      speaker_name: speakerName,
      line_type: lineType,
      text_content: trimmedLine,
      emotion_type: 'neutral',
      emotion_intensity: 'medium',
      is_generated: true
    };
  });
}

console.log('Prepared script lines for database:', scriptLines.length);
console.log('Sample line with mapped emotion:', {
  original_emotion: geminiData.scriptLines?.[0]?.emotion,
  mapped_emotion: scriptLines[0]?.emotion_type
});

// Validate script lines before returning
const validatedLines = scriptLines.filter(line => {
  if (!line.script_id || !line.text_content || line.text_content.trim() === '') {
    console.warn('Filtering out invalid line:', line);
    return false;
  }
  return true;
});

console.log('Validated lines count:', validatedLines.length);

// Return the script lines array
return validatedLines;

// Get the processed script data from previous nodes
const scriptData = $('Process Gemini Response').first().json;
const jobData = $('Validate Input').first().json;

console.log('Building response with Gemini script data:', scriptData);
console.log('Job data:', jobData);

// Build the response object
const response = {
  success: true,
  message: 'Gemini AI script generation completed successfully',
  data: {
    jobId: jobData.jobId || 'unknown',
    storyId: jobData.storyId || 'unknown',
    script: {
      title: scriptData.aiResponse?.script?.title || 'Gemini Generated Script',
      lines: scriptData.scriptLines || [],
      generatedBy: 'Gemini 2.0 Pro'
    },
    metadata: {
      characters: scriptData.aiResponse?.characters || [],
      expandedConcept: scriptData.aiResponse?.expandedConcept || '',
      generatedAt: new Date().toISOString(),
      model: 'gemini-2.0-pro',
      contextWindow: '2M tokens'
    },
    status: 'completed'
  }
};

console.log('Final Gemini response object:', response);
return response;
